const BAD_REGEX = new RegExp(
  `(${BAD_ROOTS.join("|")})[а-яёa-z]*`,
  "gi"
);

let filterEnabled = true;

chrome.storage.sync.get(["filterEnabled"], res => {
  filterEnabled = res.filterEnabled !== false;
  if (filterEnabled) init();
});

function blurText(node) {
  if (!node.nodeValue) return;

  if (BAD_REGEX.test(node.nodeValue)) {
    const span = document.createElement("span");
    span.innerHTML = node.nodeValue.replace(
      BAD_REGEX,
      match => `<span style="filter: blur(6px);">${match}</span>`
    );

    node.parentNode.replaceChild(span, node);
  }
}

function walk(node) {
  if (!filterEnabled) return;

  if (node.nodeType === Node.TEXT_NODE) {
    blurText(node);
  } else if (
    node.nodeType === Node.ELEMENT_NODE &&
    !["SCRIPT", "STYLE", "NOSCRIPT"].includes(node.tagName)
  ) {
    node.childNodes.forEach(walk);
  }
}

function init() {
  walk(document.body);

  const observer = new MutationObserver(mutations => {
    mutations.forEach(m =>
      m.addedNodes.forEach(walk)
    );
  });

  observer.observe(document.body, {
    childList: true,
    subtree: true
  });
}